import styled from 'styled-components';

export const StyledLogin = styled.div`
    @media (max-width: 900px) {
    img {
        display: none;
        width: 20rem;
        height: 10rem;
    }
    }

    display        : flex;
    justify-content: center;
    align-items    : center;
    gap            : 10rem;

    > img {
        width: 50rem;
    }

`
